public static function MapContextParam($sContextParam)
{
	if ($sContextParam == 'org_id') return 'obj_org_id';
	else return null;
}

public function SetHostObject(DBObject $oHost, $bUpdateOnChange = false)
{
	$sClass = get_class($oHost);
	$this->Set('obj_class', $sClass);
	$this->Set('obj_key', $oHost->GetKey());

	$aCallSpec = array($sClass, 'MapContextParam');
	if (is_callable($aCallSpec)) {
		$sAttCode = call_user_func($aCallSpec, 'org_id'); // Returns null when there is no mapping for this parameter
		if (MetaModel::IsValidAttCode($sClass, $sAttCode)) {
			$iOrgId = $oHost->Get($sAttCode);
			if ($iOrgId > 0) {
				if ($iOrgId != $this->Get('obj_org_id')) {
					$this->Set('obj_org_id', $iOrgId);
					if ($bUpdateOnChange) {
						$this->DBUpdate();
					}
				}
			}
		}
	}
}

public function SetDefaultOrgId()
{
	// First check that the organization CAN be fetched from the target class
	//
	$sClass = $this->Get('obj_class');
	$aCallSpec = array($sClass, 'MapContextParam');
	if (is_callable($aCallSpec)) {
		$sAttCode = call_user_func($aCallSpec, 'org_id'); // Returns null when there is no mapping for this parameter
		if (MetaModel::IsValidAttCode($sClass, $sAttCode)) {
			// Second: check that the organization CAN be fetched from the current user
			//
			if (MetaModel::IsValidClass('Person')) {
				$aCallSpec = array($sClass, 'MapContextParam');
				if (is_callable($aCallSpec)) {
					$sAttCode = call_user_func($aCallSpec, 'org_id'); // Returns null when there is no mapping for this parameter
					if (MetaModel::IsValidAttCode($sClass, $sAttCode)) {
						// OK - try it
						//
						$oCurrentPerson = MetaModel::GetObject('Person', UserRights::GetContactId(), false);
						if ($oCurrentPerson) {
							$this->Set('obj_org_id', $oCurrentPerson->Get($sAttCode));
						}
					}
				}
			}
		}
	}
}

public function Render($bEditMode = false)
{
	$bEditMode = $this->IsEditAllowed() && $bEditMode;
	// Render items
	$oItemSet = $this->Get('items_list');
	$aChecklistItems = array();
	while ($oItem = $oItemSet->Fetch()) {
			array_push($aChecklistItems, html::render('li', null, self::RenderItem($oItem, $bEditMode)));
	}
	// Render actions
	$sChecklistActions = '';
	$sChecklistTitleEdit = '';
	if ($bEditMode) {
		$sChecklistActions = html::render('div.checklist-new-item', null, array(
			html::render('a.checklist-btn.quiet', array('data-checklist-action' => 'new_item'), \Dict::S('Checklist:Button:NewItem')),
			html::render('input.checklist-item-text-input', array('name' => 'newItemText', 'maxlength' => 255, 'placeholder' => \Dict::S('Checklist:NewItemPlaceholder'))),
			html::render('div.checklist-item-actions', null, array(
				html::render('a.checklist-btn.quiet', array('data-checklist-action' => 'save_item'), \Dict::S('Checklist:Button:Save')),
				html::render('a.checklist-btn.quiet', array('data-checklist-action' => 'cancel_item'), \Dict::S('Checklist:Button:Cancel')))
			)
		));
		$sChecklistTitleEdit = html::render('input.checklist-title-text-input', array('name' => 'listTitle', 'value' => $this->Get('title'), 'maxlength' => 255));
		$sChecklistTitleEdit .= html::render('div.checklist-title-actions', null, array(
			html::render('a.checklist-btn.quiet', array('data-checklist-action' => 'edit'), \Dict::S('Checklist:Button:Edit')),
			html::render('a.checklist-btn.quiet', array('data-checklist-action' => 'delete'), \Dict::S('Checklist:Button:Delete')),
			html::render('a.checklist-btn.quiet', array('data-checklist-action' => 'save'), \Dict::S('Checklist:Button:Save')),
			html::render('a.checklist-btn.quiet', array('data-checklist-action' => 'cancel'), \Dict::S('Checklist:Button:Cancel'))
		));
	}
	// Render checklist
	$iChecklistId = $this->GetKey();
	$sChecklistName = $this->GetName(); // . " ($iChecklistId)";
	$sHtml = html::render('div.checklist', array('data-checklist-id' => $iChecklistId, 'data-checklist-name' => $sChecklistName), array(
		html::render('div.checklist-title', array('data-checklist-id' => $iChecklistId), array(
			html::render('h3.checklist-title-text', null, $sChecklistName), $sChecklistTitleEdit)),
		html::render('ol.checklist-items', null, $aChecklistItems),
		$sChecklistActions
	));
	return $sHtml;
}

public function RenderItem(ChecklistItem $oItem, $bEditMode = false)
{
	$iItemId = $oItem->GetKey();
	$sItemText = $oItem->Get('text');
	$sItemCheckedAt = $oItem->GetAsHTML('checked_at');
	$sCheckedAttr = $oItem->Get('state') == 1 ? 'checked' : '';
	$sCheckedClass = $sCheckedAttr ? 'checklist-item-state-complete' : '';
	$sDisabledAttr = $this->IsEditAllowed() ? '' : 'disabled';

	$sEditSection = '';
	if ($bEditMode) {
		$sTextInput = html::render('input.checklist-item-text-input', array('type' => 'text', 'name' => 'itemText', 'value' => $sItemText, 'maxlength' => 255));
		$sButtons = html::render('div.checklist-item-actions', null, array(
			html::render('a.checklist-btn quiet', array('data-item-action' => 'edit'), \Dict::S('Checklist:Button:Edit')),
			html::render('a.checklist-btn quiet', array('data-item-action' => 'delete'), \Dict::S('Checklist:Button:Delete')),
			html::render('a.checklist-btn quiet', array('data-item-action' => 'save'), \Dict::S('Checklist:Button:Save')),
			html::render('a.checklist-btn quiet', array('data-item-action' => 'cancel'), \Dict::S('Checklist:Button:Cancel')),
		));
		$sEditSection = $sTextInput . $sButtons;
	}

	$sHtml = html::render('div.checklist-item.' . $sCheckedClass, array('data-item-id' => $iItemId), array(
		html::render('div.checklist-item-checkbox', null,
			html::render('input', array('type' => 'checkbox', 'name' => 'itemState', $sCheckedAttr, $sDisabledAttr))
		),
		html::render('div.checklist-item-text', null, $sItemText),
		html::render('div.checklist-item-checked-at.quiet', null, $sItemCheckedAt),
		$sEditSection
	));

	return $sHtml;
}

public function FillFromTemplate(ChecklistTemplate $oTemplate)
{
	// TODO: использовать ExecAction в DBObject для шаблонов
	// $aContext = $oObj->ToArgs('this'); // TODO: для замены плейсхолдеров в шаблонах
	// // Apply context ($this->...$)
	// $sText = MetaModel::ApplyParams($oTemplate->Get('body'), $aContext);

	$this->Set('title', $oTemplate->Get('title'));
	$aItemsText = explode("\n", $oTemplate->Get('items_text'));
	$this->DBWrite();
	foreach ($aItemsText as $sItemText) {
		$oItem = MetaModel::NewObject('ChecklistItem'); // new ChecklistItem();
		$oItem->Set('checklist_id', $this->GetKey());
		$oItem->Set('text', $sItemText);
		$oItem->DBWrite();
	}
}

