						protected function OnUpdate()
{
	if ($this->IsChecked() && array_key_exists('state', $this->ListChanges())) {
		$this->SetCurrentDate('checked_at');
	} else {
		$this->Reset('checked_at');
	}
}

public static function MapContextParam($sContextParam)
{
	if ($sContextParam == 'org_id') return 'checklist_org_id';
	else return null;
}

protected function DoCreateTargetObject(DBObject $oContextObject = null, array $aContextArgs = array())
{
	if (is_null($oContextObject))
	{
		// TODO: add \Knowitop\ObjectTemplateExtension class
		throw new Exception('Cannot create a new '.$this->GetTargetClass().' from '.get_class($this).'::'.$this->GetKey().': the context object is not passed.');
	}
	$aParams = array_merge($this->ToArgs('template'), $oContextObject->ToArgs('context'), $aContextArgs);
	$sTitle = MetaModel::ApplyParams($this->Get('checklist_title'), $aParams);
	/** @var \Checklist $oChecklist */
	$oChecklist = MetaModel::NewObject('Checklist', ['title' => $sTitle]);
	$oChecklist->SetHostObject($oContextObject);
	/** @var \ormLinkSet $oItemLinkSet */
	$oItemLinkSet = $oChecklist->Get('items_list');
	$aItemsText = explode("\n", $this->Get('checklist_items'));
	foreach($aItemsText as $sItemText)
	{
		$oItem = MetaModel::NewObject('ChecklistItem', ['text' => MetaModel::ApplyParams($sItemText, $aParams)]);
		$oItemLinkSet->AddItem($oItem);
	}
	$oChecklist->Set('items_list', $oItemLinkSet);

	return $oChecklist;
}
